mapper.update:
UPDATE PLACE
SET HITSCOUNT = HITSCOUNT + 1
WHERE PLACENO = #{spotno}

mapper.select:
SELECT * FROM (
	SELECT ROWNUM AS RNUM, B.* FROM (
		SELECT *
		FROM PLACE A
		WHERE 1 = 1 

		AND MINORCATEGORY = #{minorcategory}

		ORDER BY NUM DESC
	) B
) WHERE RNUM BETWEEN #{first} AND #{last}
